                                            if (!location) {
                                                location = object.long_name;
                                            }
                                        }
                                    }
                                } else if (name === "country") {
                                    country = object.long_name;
                                }

                                if (location && country) return false;
                            });
                            if (location && country) return false;
                        });

                        userLocation = {
