        draggable: false,
        zIndex: 0
      });
      markerA.setVisible(true);

      var sw2 = currentMapViewport.getSouthWest();
      latlng = new google.maps.LatLng(sw2.lat(), sw2.lng());
      var markerB = new google.maps.Marker({
        map: map,
