              const guessValuesFromSearch = function () {
        let currentSearchFormLocationValues = {}

        const extractSearchFormLocationValue = function(key) {
          let inputValue = $('input[name="location[' + key + ']"').val();

          if (inputValue !== 'undefined') {
            currentSearchFormLocationValues[key] = inputValue;
          }
        }

        params.map(extractSearchFormLocationValue);

        return currentSearchFormLocationValues;
      };

              const guessSearchParams = function () {
        const promise = new Promise(function(resolve, reject) {
          let guessedValues = guessValuesFromSearch();
          const country = 'US';
          const countryName = 'United States';

                      if (guessedValues.country === undefined || guessedValues.country === '') {
            guessedValues.country = country
          }

                      if (guessedValues.address === undefined || guessedValues.address === '') {
            const onGeocodeSuccess = function(data) {
              params.map(function(param) {
                if (
                  (guessedValues[param] === undefined || guessedValues[param] === '')
                  && data[param] !== undefined && data[param] !== ''
                ) {
                  guessedValues[param] = data[param]
                }
              });

              if (
                (guessedValues.address === undefined || guessedValues.address === '')
                && data.formatted_address !== undefined && data.formatted_address !== ''
              ) {
                guessedValues.address = data.formatted_address
              }

              return Promise.resolve(guessedValues);
            };

            const onGeocodeFail = function(error) {
              let address = '';
              const concatWithAddress = function(item) {
                if (address !== '') {
                  address += ', ';
                }

                return address += item;
              }

              if (guessedValues.department !== undefined || guessedValues.department !== '') {
                concatWithAddress(guessedValues.department);
              }

              if (guessedValues.area !== undefined || guessedValues.area !== '') {
                concatWithAddress(guessedValues.area);
              }

              if (guessedValues.country !== undefined || guessedValues.country !== '') {
                concatWithAddress(guessedValues.country);
              }

              if (guessedValues.address !== undefined || guessedValues.address !== '') {
                guessedValues.address = address;
              }

              return Promise.resolve(guessedValues);
            }

            //If only categories selected , than location will be default France
            resolve(
              geocodeFunc("France")
                .then(onGeocodeSuccess)
                .catch(onGeocodeFail)
            );
          } else {
            resolve(guessedValues);
          }
        });

        return promise;
      };

              guessSearchParams()
        .then(function(values) {
          const makeQueryReducer = function (acc, item) {
            let value = values[item];

            if (acc !== '') {
              acc += '&';
            }

            let queryParam = 'location[' + item + ']='
            if (value !== undefined && value !== '') {
              queryParam += value
            }

            acc += queryParam;

            return acc;
          }

          const updateCategoryLink = function (index, link) {
            let $link = $(link)
            let url = new URL($link.attr('href'), window.location.origin)

            if (url.search) {
              url.search = url.search + '&' + query
            } else {
              url.search = query
            }

            $link.attr('href', url.pathname + url.search);
          }

          const query = params.reduce(makeQueryReducer, '');
          $('.category-link').each(updateCategoryLink);
        })
    }

