            //Choose the address format closest to the user's entry
            var result = null;
            var types = addressType || 'locality,political';
            for (let item of results) {
              if (item.types.join(',') === types) {
                result = item;
                break;
              }
            }

            if (result) {
              //Format geocoding
              var formattedResult = formatGeocodingResultFunc(result);

              //Translate geocoding
